import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import './Post.scss';

const Post = ({ postInfo, userInfo }) => {
  const [followed,setFollow] = useState(false)
  const [like,setLike] = useState(false);
  const handleChange = (state) => {
    switch (state){
      case "follow":
        setFollow(!followed);
        break;
      case "like":
        setLike(!like);
        break; 
      default:
        console.log("error")
    }

  }
  return (
    <article className="post">
    <div className="post__header">
        <div className="user">
        <Link to={`/${userInfo["username"]}`} className="user__thumb">
            <img src={userInfo["avatar"]} alt={userInfo["name"]}/>
          </Link>
          <Link to={`/${userInfo["username"]}`}>
            <span className="user__name">T'Challa</span>
          </Link>
        </div>

        <button className="post__context" onClick={()=>handleChange("follow")}>
          { followed ? <span className="follow-btn is-following">Seguindo</span> : <span className="follow-btn">Seguir</span> }
        </button>
      </div>
      <div className="post__figure">
        <img src={postInfo["imageUrl"]} alt={userInfo["name"]}/>
      </div>
      <div className="post__controls">
        <button className="post__control" onClick={() =>handleChange("like")}>
          {like ? <i className="fas fa-heart"></i> : <i className="far fa-heart"></i>}
        </button>
        <div className="user">
        {console.log(postInfo["comments"][0])}

          <span>
            Curtido por <Link to={`/${userInfo["name"]}`}> {postInfo["comments"][0]["username"]} </Link> e outras <Link to={`/${userInfo["name"]}`}>{postInfo["comments"].length -1 } pessoas</Link>
          </span>
        </div>
      </div>
    </article>
  );
};

export default Post;
