import {createStore} from "redux";

const INITIAL_STATE = {
    app : {
        environment: "production",
        name: "react-spotify",
        version: "0.1.0"
    },
    auth: {
       accessToken: "", 
       errorMessage: "",
       expirationTime: "",
       expiresIn: "",
       isLogged: null,
       tokenType: ""
    },
    content: {
        categories: [],
        playlists: [],
        tracks: [],
        playingNowId: null,
        playingNowTrack: null,
        playerHeigh: 0,
        status: "idle",
        errorMessage:""
    },
    user: {
        email: "",
        errorMessage:"",
        name: "",
        status: null,
        thumb: "",
    }
};


function reducer(state= INITIAL_STATE, action){
    const payload = action.payload;
    switch (action.type) {
        case "SET_USER":
            return  {...state, action: payload }
        case "SET_AUTH":
            console.log(action.accessToken)
            console.log({...state, auth: {...state.auth, accessToken: payload.accessToken, expiresIn: payload.accessToken, tokenType: payload.tokenType} });
            return  {...state, auth: {...state.auth, accessToken: payload.accessToken, expiresIn: payload.accessToken, tokenType: payload.tokenType} }    
        case "CHANGE_STATUS":
            return {...state, content: {...state.content , status: payload} }
        default:
            return state;
    }
    
}

const store = createStore(reducer);
export default store;
