import {createStore} from "redux";

const INITIAL_STATE = {
    app : {
        environment: "production",
        name: "react-spotify",
        version: "0.1.0"
    },
    auth: {
       accessToken: "", 
       errorMessage: "",
       expirationTime: "",
       expiresIn: "",
       isLogged: null,
       tokenType: ""
    },
    content: {
        categories: [],
        playlists: [],
        tracks: [],
        playingNowId: null,
        playingNowTrack: null,
        playerHeigh: 0,
        status: "idle",
        errorMessage:""
    },
    user: {
        email: "",
        errorMessage:"",
        name: "",
        status: null,
        thumb: "",
    }
};


function reducer(state= INITIAL_STATE, action){
    switch (action.type) {
        case "SET_USER":
            return [ ...state, action.payload ]
        case "SET_AUTH":
            return [ ...state, action.payload ]    
        case "CHANGE_CONTENT":
            return [ ...state, action.payload]
        default:
            return state;
    }
    
}

const store = createStore(reducer);
export default store;
