import React, {useState,useEffect} from 'react';
import Image from "../../assets/images/app-intro-1.jpg"
import { Loading } from '../../components';
import { Redirect } from 'react-router-dom';
import { getToken } from "../../tools/splitUrl";
import { DashBoard } from "../../Pages/index";

import "./Authorize.scss"

const Authorize = () => {
    const [autorized, setAutorized] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if(window.location.hash !== ""){
            const hash = getToken(window.location.hash);
            localStorage.setItem("token",hash["access_token"]);
            localStorage.setItem("expires_in",hash["expires_in"]);
            localStorage.setItem("token_type",hash["token_type"]);
            setLoading(false);
            setAutorized(true);

        }else{
            setAutorized(false);
        }
    }, [])

    return (
        <div className="callback" data-testid="callback">
            {
            autorized === true && loading === false ? <DashBoard></DashBoard> : autorized !== false ? <Loading text="Autenticando..."></Loading> : <Redirect to={{ pathname:"/", state:{from:window.location.href}}}></Redirect>
            }    
        </div>
    )
}

export default Authorize
