import React, {useState,useEffect} from 'react';
import { Loading } from '../../components';
import { Redirect } from 'react-router-dom';
import { getToken } from "../../tools/splitUrl";
import { connect } from "react-redux"
import "./Authorize.scss"

const Authorize = ({auth,content,state}) => {
    const [autorized, setAutorized] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if(window.location.hash !== ""){
            const hash = getToken(window.location.hash);
            localStorage.setItem("token",hash["access_token"]);
            localStorage.setItem("expires_in",hash["expires_in"]);
            localStorage.setItem("token_type",hash["token_type"]);
            setLoading(false);
            setAutorized(true);

        }else{
            setAutorized(false);
        }
    }, [])

    return (

        <div className="callback" data-testid="callback">
            {console.log(auth)}
            {console.log(content)}
            {console.log(state)}


            {
            autorized === true && loading === false ? <Redirect to="/dashboard"></Redirect> : autorized !== false ? <Loading text="Autenticando..."></Loading> : <Redirect to={{ pathname:"/", state:{from:window.location.href}}}></Redirect>
            }    
        </div>
    )
}

export default connect(state => ({auth: state.auth, content: state.content, state:state}))(Authorize);
