                <strong>
                    Com mÃºsica
                    <span>ðŸ’˜</span>
                </strong>
            </h1>
            <button onClick={()=> (<Redirect to="/batata"></Redirect>)}>Batata</button>
            <a onClick={() =>{dispatch(Actions.changeStatus("Loading"))}} className="login__auth-button" href={`${spotify.authorizationURL}?client_id=${spotify.clientId}${(spotify.scopes ? '&scope=' + encodeURIComponent(spotify.scopes) : '')}&redirect_uri=${encodeURIComponent(spotify.redirectUrl)}&response_type=token&show_dialog=true`}>Entrar no Spotify</a>
        </div>
    </main>);

export default connect(state => ({content: state.content}))(Login);
