import {createStore} from "redux";

const InitialState = {
    app : {
        environment: "production",
        name: "react-spotify",
        version: "0.1.0"
    },
    auth: {
       accessToken: "", 
       errorMessage: "",
       expirationTime: "",
       expiresIn: "",
       isLogged: false,
       tokenType: ""
    },
    content: {
        categories: [],
        playlists: [],
        tracks: [],
        playingNowId: null,
        playingNowTrack: null,
        playerHeigh: 0,
        status: "idle",
        errorMessage:""
    },
    user: {
        email: "",
        errorMessage:"",
        name: "",
        status: null,
        thumb: "",
    }
};


function reducer(state= InitialState, action){
    return state

}

const store = createStore(reducer);
export default store;
