import React, {useState,useEffect} from 'react';
import * as Actions from "../actions";
import {connect} from 'react-redux';
import {Dashboard,Topbar,Categories} from '../containers';
import {getProfileUser} from "../tools/handleRequests"


const DashboardRoute = ({user,auth,dispatch}) => {
    useEffect(() => {
        async function teste(){
            const {images,email,display_name,} = await getProfileUser(auth.accessToken)
                .then(res => res.data)
                .catch(error => 
                    {
                        if(error === 401){
                            return dispatch(Actions.logout)
                        } 
                        dispatch(Actions.loginError(error))})
            dispatch(Actions.setUser({email: email, name: display_name, thumb: images[0].url}))
        }
        teste()
    }, [auth])
    return (
        <Dashboard>
            {console.log(user)}
            <Topbar></Topbar>
            <Categories></Categories>
        </Dashboard>
    )
}
export default connect(state => ({user: state.user, auth: state.auth}))(DashboardRoute)
