import React, {useState,useEffect} from 'react'

import {Authorize} from "../../containers"
import { Loading } from '../../components';
import { Redirect } from 'react-router-dom';
import { getToken } from "../../tools/splitUrl"


const AutorizePage = () => {
    const [autorized, setAutorized] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if(window.location.hash !== ""){
            console.log(window.location.hash);
            const hash = getToken(window.location.hash);
            console.log(hash);
        }else{
            setAutorized(false);
        }
    }, [])
    
    return (
        <div className="callback">
            {console.log(window.location.hash === "")}
            {
            autorized === true && loading === false ? <Authorize></Authorize> : autorized !== false ? <Loading></Loading> : <Redirect to={{ pathname:"/", state:{from:window.location.href}}}></Redirect>
            }    
        </div>
    )
}

export default AutorizePage
