import React, {useState,useEffect} from 'react';
import { Loading } from '../../components';
import { Redirect } from 'react-router-dom';
import { getToken } from "../../tools/splitUrl";
import { connect } from "react-redux"
import "./Authorize.scss"

const Authorize = ({auth,content, dispatch}) => {

    useEffect(() => {

        if(window.location.hash !== ""){
            const hash = getToken(window.location.hash);
            localStorage.setItem("token",hash["access_token"]);
            localStorage.setItem("expires_in",hash["expires_in"]);
            localStorage.setItem("token_type",hash["token_type"]);


        }else{

        }
    }, [])

    return (

        <div className="callback" data-testid="callback">
            {console.log(auth)}
            {console.log(content)}

            {
            auth.isLogged === true && content.status === "Loading" ? <Redirect to="/dashboard"></Redirect> : auth.isLogged !== false ? <Loading text="Autenticando..."></Loading> : <Redirect to={{ pathname:"/", state:{from:window.location.href}}}></Redirect>
            }    
        </div>
    )
}

export default connect(state => ({auth: state.auth, content: state.content,}))(Authorize);
