import axios from "axios"

import {config} from "../config"

const {spotify} = config;

export const api = axios.create({
    baseUrl: spotify.webAPI
})


export const getProfileUser = async (accesToken) =>{
    return await api.get("/me",{headers:{"Authorization":`Bearer ${accesToken}`}});
}

export const getCategories = () =>{
    return api.get("/browse/categories?country=BR&locale=pt_BR")
}

export const getCategoryPlaylists = (categoryId) =>{
    return api.get(`/browse/categories/${categoryId}/playlists`)
}

export const getPlayListsTracks = (playlistId) => {
    return api.get(`/playlists/${playlistId}/tracks`)
}
