import React, {useEffect} from 'react';
import {Switch,useRouteMatch} from "react-router-dom"

import * as Actions from "../actions";

import {connect} from 'react-redux';

import {Dashboard,Topbar,Categories} from '../containers';
import {getProfileUser,getCategories} from "../tools/handleRequests"


const DashboardRoute = ({cate,user,auth,dispatch}) => {
    const { url, path } = useRouteMatch()
    useEffect(() => {
        async function getUser(){
            const {images,email,display_name,} = await getProfileUser(auth.accessToken)
                .then(res => res.data)
                .catch(error => 
                    {
                        if(error === 401){
                            return dispatch(Actions.logout)
                        } 
                        dispatch(Actions.loginError(error))})
            dispatch(Actions.setUser({email: email, name: display_name, thumb: images[0].url}))
        }
        getUser()
    }, [auth,dispatch])
    useEffect(() => {
        async function setCategories(){
            const {categories} = await getCategories(auth.accessToken)
                .then(res=>res.data)
                .catch(error=> {
                    if(error ===401){
                        return dispatch(Actions.logout);
                    }
                    return dispatch(Actions)
                })
        }
        setCategories();
    }, [auth,dispatch])
    return (
        <Dashboard>
            <Topbar></Topbar>
            <Categories></Categories>
        </Dashboard>
    )
}
export default connect(state => ({user: state.user, auth: state.auth, categories: state.categories}))(DashboardRoute)
