export const setUser = (user) => {
    return {
        type: "SET_USER",
        payload: user
    }
}
export const setAuth = (auth) =>{
    return {
        type: "SET_AUTH",
        payload:  auth
    }
}
export const changeStatus = (content) =>{
    return {
        type: "CHANGE_STATUS",
        payload:  content
    }
} 

export const logout = () => {
    return {
        type: "USER__LOGOUT"
    }
}

export const loginError = (error) =>{
    return {
        type: "LOGIN__ERROR",
        payload: error
    }
}

export const getCategoriesSuccess = (items)=>{
    return {
        type: "GET_CATEGORIES_SUCCESS",
        payload: items
    }
}
