export const setUser = (user) => {
    return {
        type: "SET_USER",
        payload: {user: user}
    }
}
export const setAuth = (auth) =>{
    return {
        type: "SET_AUTH",
        payload: {auth: auth}
    }
}
export const changeContent = (content) =>{
    return {
        type: "CHANGE_CONTENT",
        payload: {content: content}
    }
}
